@Serializable
data class WeiboResponse(
  val user: WeiboUser,
  val weibo: Array<WeiboModel>
)

@Serializable
data class WeiboUser(val screen_name: String, val profile_image_url: String)

@Serializable
data class WeiboModel(
  @Transient
  var user_name: String = "",
  @Transient
  var avatar_url: String = "",
  val source: String,
  val text: String,
  val created_at: String,
  val pics: String,
  val topics: String,
  val attitudes_count: Int,
  val comments_count: Int,
  val reposts_count: Int
)

fun WeiboModel.createTextSpans(): List<TextSpan>? {
  if (topics.isEmpty()) {
    return null
  }
  return topics.split(",").mapNotNull {
    val value = "#$it#"
    val startIndex = text.indexOf(value)
    val endIndex = startIndex + value.length - 1
    if (startIndex == -1) {
      null
    } else {
      TextSpan(startIndex, endIndex, Color.Orange, 15.dp, FontStyle.Normal)
    }
  }
}
